import styled from 'styled-components';
// import Bg from './img/Bg.jpg';

export const HomeDiv = styled.div`
    > h1 {
        font-size: 32px;
        margin: 0;
    }

    > button {
        padding: 10px 30px;
        margin-top: 80px;
    }
`
// export default HomeDiv

export const ContainerDiv = styled.div`
    display: flex;
    flex-direction: column;
`

export const ContentDiv = styled.div`
    height: 100%;
    display: flex;
    justify-content: center;
    align-items: center;

    .contentWrapper {
        font-family: 'gaegu';

        h1 {
                font-size: 1rem;
                font-weight: 700;
            }

        p {
            font-weight: 400;
            }

        .questionBox {
            >p {
                margin-top: 1rem;
            }
        }

        .answerBox {
            margin-top: 54px;
            display: flex;
            align-items: center;
            flex-direction: column;

            .ans {
                max-width: 20rem;
                max-height: 14.33333rem;
                padding: 13px;
                border-radius: 20px;
                border: 1px solid red;

                img {
                    margin-top: 1rem;
                }

                p {
                    border: 1px solid red;
                    max-width: 17.86rem;
                    margin-top: 1.6rem;
                }

            .ans2 {
                margin-top: 23px;
            }
        }
    }
`

export const BannerHeader = styled.header`
    width: 100%;

    .box {
        background-color: white;
        width: inherit;
        height: 82px;
    }
    .gauge {
            height: 3px;
            width: inherit;
            background-color: rgba(238, 238, 238, 1);

        }


`
