import styled from 'styled-components';
import backgroundImg from './img'

export const Outbox = styled.div`
    /* display: flex;
    vertical-align: center;
    align-items: center; */
    background: url(${backgroundImg})
    background-size: 100% 100%;
    text-align: center;
    width: 100vw;
    height: 100vh;
    margin: 0 auto;

    @media screen and (min-width: 500px) {
        width: 500px;
    }

`
